<div class="m-10">
    <h1 class="text-2xl mb-5">Medical Background</h1>
    <div class="grid gap-6 mb-6 md:grid-cols-2">
        <div class="mb-2">
            <label for="base-input" class="block mb-2  text-md font-medium text-gray-900 dark:text-white">Name</label>
            <input type="text" id="base-input" wire:model.debounce.500ms="mbName" class="bg-gray-50 border border-gray-300 text-gray-900 text-md rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
        </div>
        <div class="mb-2">
            <label for="base-input" class="block mb-2  text-md font-medium text-gray-900 dark:text-white">Date</label>
            <input type="text" id="base-input" wire:model.debounce.500ms="mbRelationship" class="bg-gray-50 border border-gray-300 text-gray-900 text-md rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
        </div>
        <div class="mb-2">
            <label for="base-input" class="block mb-2  text-md font-medium text-gray-900 dark:text-white">Address</label>
            <input type="text" id="base-input" wire:model.debounce.500ms="mbAddress" class="bg-gray-50 border border-gray-300 text-gray-900 text-md rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
        </div>
        <div class="mb-2">
            <label for="base-input" class="block mb-2  text-md font-medium text-gray-900 dark:text-white">Contact</label>
            <input type="number" id="base-input" wire:model.debounce.500ms="mbContact" class="bg-gray-50 border border-gray-300 text-gray-900 text-md rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
        </div>
        <div class="mb-2">
            <label for="base-input" class="block mb-2  text-md font-medium text-gray-900 dark:text-white">Email Address</label>
            <input type="number" id="base-input" wire:model.debounce.500ms="mbContact" class="bg-gray-50 border border-gray-300 text-gray-900 text-md rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
        </div>
        <div class="mb-2">
            <label for="base-input" class="block mb-2  text-md font-medium text-gray-900 dark:text-white">Illness</label>
            <input type="number" id="base-input" wire:model.debounce.500ms="mbContact" class="bg-gray-50 border border-gray-300 text-gray-900 text-md rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
        </div>
        <div class="mb-2">
            <label for="base-input" class="block mb-2  text-md font-medium text-gray-900 dark:text-white">Allergies</label>
            <input type="number" id="base-input" wire:model.debounce.500ms="mbContact" class="bg-gray-50 border border-gray-300 text-gray-900 text-md rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
        </div>
        <div class="mb-2">
            <label for="base-input" class="block mb-2  text-md font-medium text-gray-900 dark:text-white">Treatment</label>
            <input type="number" id="base-input" wire:model.debounce.500ms="mbContact" class="bg-gray-50 border border-gray-300 text-gray-900 text-md rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
        </div>
        <div class="mb-2">
            <label for="base-input" class="block mb-2  text-md font-medium text-gray-900 dark:text-white">Physician</label>
            <input type="number" id="base-input" wire:model.debounce.500ms="mbContact" class="bg-gray-50 border border-gray-300 text-gray-900 text-md rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
        </div>
    </div>
</div>